# Counting nr of instructions
instructions_length = len(instructions)
print(f"\nNumber of moves: {instructions_length}")

floors_up = 0
floors_down = 0
basement_position = 0

for i in range(instructions_length):
    if instructions[i] == "(":
        floors_up += 1     
    else: 
        floors_down +=1
    if basement_position == 0:
        if floors_up - floors_down < 0:
            basement_position = i + 1

floors_down = instructions_length - floors_up
print(f"Santa ends up on floor {floors_up - floors_down}")
print(f"Santa entered the basement for the first time at position {basement_position}")



        